//Fragment can be used as a substitute for div to wrap elements with disturbing style
import React,{ Fragment } from 'react'
// Link as we don't want to refresh page when we click on a href everytime
// withRouter as we need to pass props to child components
import{Link, withRouter} from 'react-router-dom'
import { signout, isAuthenticated } from "../auth";
import { itemTotal } from "./cartHelpers";

const isActive = (history, path) => {
    if (history.location.pathname === path) {
        return { color: "#ff9900" };
    } else {
        return { color: "#ffffff" };
    }
};

//Destructing history from props

const Menu = ({history}) => (
	 <div>
	     <ul className ="nav nav-tabs bg-primary">
	       <li className="nav-item">
                <Link className="nav-link" style={isActive(history, "/")} to = "/">Home </Link>
	       </li>
            <li className="nav-item">
                <Link className="nav-link" style={isActive(history, "/shop")} to = "/shop">Shop </Link>
           </li>

           <li className="nav-item">
                <Link
                    className="nav-link"
                    style={isActive(history, "/cart")}
                    to="/cart"
                >
                    Cart{" "}
                    <sup>
                        <small className="cart-badge">{itemTotal()}</small>
                    </sup>
                </Link>
            </li>


            {isAuthenticated() && isAuthenticated().user.role === 0 && (
                <li className="nav-item">
                    <Link
                        className="nav-link"
                        style={isActive(history, "/user/dashboard")}
                        to="/user/dashboard"
                    >
                        Dashboard
                    </Link>
                </li>
            )}

            {isAuthenticated() && isAuthenticated().user.role === 1 && (
                <li className="nav-item">
                    <Link
                        className="nav-link"
                        style={isActive(history, "/admin/dashboard")}
                        to="/admin/dashboard"
                    >
                        Dashboard
                    </Link>
                </li>
            )}	       

            {!isAuthenticated() && (
            	//Fragment can be used as a substitute for div to wrap elements with disturbing style
            	//Using div here breaks page
                <Fragment>
                    <li className="nav-item">
                        <Link
                            className="nav-link"
                            style={isActive(history, "/signin")}
                            to="/signin"
                        >
                            Signin
                        </Link>
                    </li>

                    <li className="nav-item">
                        <Link
                            className="nav-link"
                            style={isActive(history, "/signup")}
                            to="/signup"
                        >
                            Signup
                        </Link>
                    </li>
                </Fragment>
            )}

            {isAuthenticated() && (
                <li className="nav-item">
                    <span
                        className="nav-link"
                        style={{ cursor: "pointer", color: "#ffffff" }}
                        onClick={() =>
                            signout(() => {
                                history.push("/");
                            })
                        }
                    >
                        Signout
                    </span>
                </li>
            )} 
	     </ul>
	 </div>
	);


export default withRouter(Menu);
