import React, { useState, useEffect } from 'react';
import Layout from '../core/Layout';
import { isAuthenticated } from '../auth';
import { Link } from 'react-router-dom';
import { createProduct } from './apiAdmin';


const AddProduct = () => {
	const [values, setValues] = useState({
        name: '',
        description: '',
        price: '',
        categories: [],
        category: '',
        shipping: '',
        quantity: '',
        photo: '',
        loading: false,
        error: '',
        createdProduct: '',
        redirectToProfile: false,
        formData: ''
    });

    const { user, token } = isAuthenticated();

    const {
        name,
        description,
        price,
        categories,
        category,
        shipping,
        quantity,
        loading,
        error,
        createdProduct,
        redirectToProfile,
        formData
    } = values;


    //useeffect is used to load formdata with state values once component loads and also once forms values are updated in state
    //This is shorter version of component did mount

      const handleChange = name => event => {
        const value = name === 'photo' ? event.target.files[0] : event.target.value;
        //formadata is used to send data to backend as Form instead of json
        formData.set(name, value);
        setValues({ ...values, [name]: value });
    };

    return (
        <Layout title="Add a new product" description={`G'day ${user.name}, ready to add a new product?`}>
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    .....
                </div>
            </div>
        </Layout>
    );
};

export default AddProduct;
